import { useEffect, useState } from 'react';
import './Cryptic.style.css';
const Cryptic = () => {
  const [passList, setPassList] = useState('');
  const [toUsePassword, setToUsePassword] = useState();
  const [message, setMessage] = useState('');
  useEffect(() => {
    fetch('http://localhost:3001/pass-list', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        token: 'Bearer' + sessionStorage.getItem('token'),
      },
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setPassList(data);
      })
      .catch((error) => console.log(error));
  }, []);


  const dataToEncrypt = {
    key: toUsePassword,
    message,
  }

  const encryptRequest = () => {
    console.log('encryptRequest Working');
    fetch('http://localhost:3001/encrypt', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json',
        token: 'Bearer' + sessionStorage.getItem('token'),
      },
      body: JSON.stringify(dataToEncrypt),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log(data);
        setPassList(data);
      })
      .catch((error) => console.log(error));
  };

  return (
    <>
      <div>
        {passList.length > 1 ? (
          <select
            name=''
            id=''
            onChange={(e) => {
              setToUsePassword(e.target.value);
            }}
          >
            {passList.map((option) => (
              <option key={option._id} value={option.passwordOrg}>
                {option.title}
              </option>
            ))}
          </select>
        ) : null}
      </div>
      <div className='w-full cryptic-container'>
        <div>
          <input
            type='text'
            name=''
            id=''
            className='message'
            onChange={(e) => setMessage(e.target.value)}
          />
        </div>
        <div>
          <button onClick={() => encryptRequest()}>Encrypt</button>
          <button>Decrypt</button>
        </div>
        <div>
          <input type='text' className='message message-output' />
        </div>
      </div>
    </>
  );
};

export default Cryptic;
